import React from 'react';
import './Dashboard.css'
import { Link, Routes, Route} from 'react-router-dom';
import AllTasks from './AllTasks';
const Dashboard = () => {

    
    const headingStyle = {
        position: 'absolute',
        top: '-20px', 
        left: '50%',
        transform: 'translateX(-50%)',
        fontSize: '36px',
        color:'#fff'
      };

      const headingStyle1 = {
        position: 'absolute',
        top: '-10px', 
        left: '16%',
        transform: 'translateX(-150%)',
        fontSize: '30px',
        color:'white' 
      };

  return (
    <div className="dashboard-container">
   
      <div className="sidenav">
        <h2 style={headingStyle1}>DashBoard</h2>
        <Link to="/add-task">Add Task</Link> 
        <Link to="/all-tasks">All Tasks</Link>
        
        <a href="#">Reminder</a>
        <a href="#">Settings</a>
        <a href="#">Logout</a>
      </div>
      <div className="main-content">
        <h1 style={headingStyle}>Welcome to Task Management</h1>
         <Routes>
          <Route path="/all-tasks" element={<AllTasks />} /> {/* Define route for AllTasks component */}
        </Routes>
      </div>
    </div>
  );
};

export default Dashboard;
