import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import UserService from '../services/UserService';

const RegisterPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [email, setEmail] = useState('');
  const navigate = useNavigate();

  const handleRegister = async () => {
    try {
      await UserService.register({ username, password, email });
      alert('User registered successfully.');
      navigate('/login'); // Redirect to login page after successful registration
    } catch (error) {
      alert('Username already exists.');
    }
  };

  const backgroundStyle = {
    backgroundImage: `url('https://png.pngtree.com/thumb_back/fh260/background/20220522/pngtree-business-team-banner-task-management-image_1374989.jpg')`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    height: '100vh', 
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  };

  const formContainerStyle = {
    maxWidth: '800px',
    padding: '55px',
    borderRadius: '8px',
    backgroundColor: 'rgba(255, 255, 255, 0.8)', 
    marginRight: '500px', 
    display: 'flex', 
     flexDirection: 'column', 
     alignItems: 'center', // Center items horizontally
  };

  const inputStyle = {
    width: '100%', // Make input boxes take up full width
    padding: '10px', // Add padding for better appearance
    marginBottom: '20px', // Add some space between input boxes
    borderRadius: '5px', // Add rounded corners
    border: '1px solid #ccc', // Add border for better visibility
  };

  return (
    <div style={backgroundStyle}>
      <div className="container" style={formContainerStyle}>
        <h2 className="text-center mb-4">Register</h2>
        <form>
          <div className="mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              style={inputStyle}
            />
          </div>
          <div className="mb-3">
            <input
              type="password"
              className="form-control"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={inputStyle}
            />
          </div>
          <div className="mb-3">
            <input
              type="email"
              className="form-control"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              style={inputStyle}
            />
          </div>
          <button 
            type="button" 
            className="btn btn-primary w-100 mt-3" // Added mt-3 class for margin top
            style={{ borderRadius: '8px', backgroundColor: 'green', color: '#fff', position: 'absolute' }} // Added inline style for border radius
            onClick={handleRegister}
          >
            Register
          </button>
        </form>
      </div>
    </div>
  );
};

export default RegisterPage;
