import React from 'react';

const WelcomePage = () => {
  const backgroundStyle = {
    backgroundImage: `url('https://png.pngtree.com/thumb_back/fh260/background/20220427/pngtree-project-management-banner-delivery-business-image_1091566.jpg')`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    height: '100vh', // Set the height of the background to fill the viewport
    display: 'flex',
    flexDirection: 'column', // Ensure flex container stacks items vertically
    alignItems: 'center', // Center items horizontally
    justifyContent: 'flex-start', 
    alignItems: 'center',
    justifyContent: 'center',
    color: '#fff', // Set text color to white for contrast
    textAlign: 'center'
  };

  const headingStyle = {
    marginTop: '20vh' // Adjust the top margin to position the heading
  };

  return (
    <div style={backgroundStyle}>
      <h1>Welcome to Task Manager</h1>
      
    </div>
  );
};

export default WelcomePage;
