import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Import Axios for making HTTP requests

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      const response = await axios.post('/api/login', { // Change the endpoint to your login endpoint
        username,
        password
      });

      if (response.status === 200) { // Assuming status 200 indicates successful login
        // If login is successful, redirect to a dashboard or home page
        navigate('/dashboard');
      } else {
        alert('Invalid username or password.');
      }
    } catch (error) {
      alert('Invalid username or password.');
    }
  };

  return (
    <div style={backgroundStyle}>
      <div className="container" style={formContainerStyle}>
        <h2 className="text-center mb-4">Login</h2>
        <form>
          <div className="mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              style={inputStyle}
            />
          </div>
          <div className="mb-3">
            <input
              type="password"
              className="form-control"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={inputStyle}
            />
          </div>
          <button 
            type="button" 
            className="btn btn-primary w-100 mt-3" // Added mt-3 class for margin top
            style={{ borderRadius: '8px',backgroundColor:'green',color:'#fff' }} // Added inline style for border radius
            onClick={handleLogin}
          >
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

export default LoginPage;
