import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, Routes, Route, useNavigate } from 'react-router-dom';
import AllTasks from './AllTasks';

const Dashboard = () => {
  const navigate = useNavigate();
  const [upcomingTasks, setUpcomingTasks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  useEffect(() => {
    const fetchUpcomingTasks = async () => {
      const credentials = "yamini:yamini";
      const encodedCredentials = btoa(credentials);
      try {
        const response = await axios.get('http://localhost:8080/api/tasks/upcoming', {
          headers: {
            'Authorization': `Basic ${encodedCredentials}`
          }
        });
        setUpcomingTasks(response.data);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching upcoming tasks:', error);
        setIsLoading(false);
      }
    };
    fetchUpcomingTasks();
  }, []);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const handleLogout = () => {
    navigate('/');
  };

  const dropdownStyle = {
    display: isDropdownOpen ? 'block' : 'none',
    position: 'absolute',
    backgroundColor: '#f9f9f9',
    minWidth: '160px',
    boxShadow: '0px 8px 16px 0px rgba(0,0,0,0.2)',
    zIndex: '1',
  };

  return (
    <div className="dashboard-container">
      <div className="sidenav">
        <h2>DashBoard</h2>
        <Link to="/add-task">Add Task</Link>
        <Link to="/all-tasks">All Tasks</Link>
        <a href="#">Reminder</a>
        <div className="dropdown">
          <Link to="#" className="dropbtn" onClick={toggleDropdown}>Settings</Link>
          <div className="dropdown-content" style={dropdownStyle}>
            <Link to="#">Notifications</Link>
            <Link to="#">Theme</Link>
            <Link to="#">Sharing Enable</Link>
          </div>
        </div>
        <a href="#" onClick={handleLogout}>Logout</a>
      </div>
      <div className="main-content">
        <h1>Welcome to Task Management</h1>
        <h2>Upcoming Tasks</h2>
        {isLoading ? (
          <p>Loading upcoming tasks...</p>
        ) : (
          <ul>
            {upcomingTasks.map(task => (
              <li key={task.id}>{task.name} - {task.issuedDate}</li>
            ))}
          </ul>
        )}
        <Routes>
          <Route path="/all-tasks" element={<AllTasks />} />
        </Routes>
      </div>
    </div>
  );
};

export default Dashboard;
