import React,{useState} from 'react';
import './Dashboard.css'
import { Link, useNavigate} from 'react-router-dom';
import UpcomingTasks from './UpcomingTasks';
import { BsPersonFill } from 'react-icons/bs';
const Dashboard = () => {
  const navigate = useNavigate();

  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const toggleDropdown = () => {
      setIsDropdownOpen(!isDropdownOpen);
  };

  const handleLogout = () => {
   
    navigate('/');
  };

  const dropdownStyle = {
      display: isDropdownOpen ? 'block' : 'none',
      position: 'absolute',
      backgroundColor: '#f9f9f9',
      minWidth: '160px',
      boxShadow: '0px 8px 16px 0px rgba(0,0,0,0.2)',
      zIndex: '1',
  };
 

      const headingStyle = {
        position: 'absolute',
        top: '0', 
        left: '0',
        width: '100%',
        textAlign: 'center',
        fontSize: '36px',
        color:'#fff',
        backgroundColor: 'rgb(158, 141, 225)',
        padding: '10px 0', 
        margin: '0', // Remove default margin
        zIndex: '1', // Ensure the heading is above other elements
      };

      const iconContainerStyle = {
        position: 'absolute',
        top: '0',
        right: '0',
        width: '120px',
        height: '65px',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      };
    
    

      const iconStyle = {
        position: 'absolute',
        top: '0',
        left: '60px',
        width: '120px', 
        height: '65px',
      };


  return (
    <div className="dashboard-container">
   
      <div className="sidenav">
      <h2 style={headingStyle}>
          <img src="https://i.pinimg.com/564x/f8/98/bf/f898bfb34a80f0784e1417c86a096e13.jpg" alt="icon" style={iconStyle} />
          DashBoard
          <div style={iconContainerStyle}>
            <BsPersonFill />
          </div>
        </h2>
        <Link to="/add-task">Add Task</Link> 
        <Link to="/all-tasks">All Tasks</Link>
        <a href="#">Reminder</a>
        <div className="dropdown">
        <Link to="#" className="dropbtn" onClick={toggleDropdown}>Settings</Link>
                    <div className="dropdown-content" style={dropdownStyle}>
                        <Link to="#">Notifications</Link>
                        <Link to="#">Theme</Link>
                        <Link to="#">Sharing Enable</Link>
                    </div>
                </div>
        <a href="#" onClick={handleLogout}>Logout</a>
      </div>
      <div className="main-content">
      <UpcomingTasks />
         
      </div>
    </div>
  );
};

export default Dashboard;
