import React,{useState} from 'react';
import './Dashboard.css'
import { Link, Routes, Route,useNavigate} from 'react-router-dom';
import AllTasks from './AllTasks';
const Dashboard = () => {
  const navigate = useNavigate();

  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const toggleDropdown = () => {
      setIsDropdownOpen(!isDropdownOpen);
  };

  const handleLogout = () => {
   
    navigate('/');
  };

  const dropdownStyle = {
      display: isDropdownOpen ? 'block' : 'none',
      position: 'absolute',
      backgroundColor: '#f9f9f9',
      minWidth: '160px',
      boxShadow: '0px 8px 16px 0px rgba(0,0,0,0.2)',
      zIndex: '1',
  };
 

  const headingStyle = {
    position: 'absolute',
    top: '0',
    left: '0',
    width: '100%',
    textAlign: 'center',
    fontSize: '36px',
    color:'#fff',
    backgroundColor: 'rgb(158, 141, 225)',
    padding: '10px 0',
    margin: '0',
    zIndex: '1',
    display: 'flex',
    alignItems: 'center', // Center vertically
    justifyContent: 'flex-start', // Align items to the start
    paddingLeft: '20px', // Add left padding to create space for the icon
    backgroundImage: 'url(https://images-platform.99static.com/8mc2pZDV0s_nXHMZOF--H-QhZzY=/500x500/top/smart/99designs-contests-attachments/20/20319/attachment_20319607)',
    backgroundRepeat: 'no-repeat',
    backgroundSize: 'contain', // Adjust the size of the background image
    backgroundPosition: 'left center', // Position the image to the left center
  };
    

  return (
    <div className="dashboard-container">
   
      <div className="sidenav">
        <h2 style={headingStyle1}>Task DashBoard</h2>
        <Link to="/add-task">Add Task</Link> 
        <Link to="/all-tasks">All Tasks</Link>
        <a href="#">Reminder</a>
        <div className="dropdown">
        <Link to="#" className="dropbtn" onClick={toggleDropdown}>Settings</Link>
                    <div className="dropdown-content" style={dropdownStyle}>
                        <Link to="#">Notifications</Link>
                        <Link to="#">Theme</Link>
                        <Link to="#">Sharing Enable</Link>
                    </div>
                </div>
        
        
        <a href="#" onClick={handleLogout}>Logout</a>
      </div>
      <div className="main-content">
       
         <Routes>
          <Route path="/all-tasks" element={<AllTasks />} /> {/* Define route for AllTasks component */}
        </Routes>
      </div>
    </div>
  );
};

export default Dashboard;
