import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import UserService from '../services/UserService';

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      // Call the UserService login function with username and password
      await UserService.login({ username, password });
      // If login is successful, redirect to a dashboard or home page
      navigate('/dashboard');
    } catch (error) {
      alert('Invalid username or password.');
    }
  };

  return (
    <div style={backgroundStyle}>
      <div className="container" style={formContainerStyle}>
        <h2 className="text-center mb-4">Login</h2>
        <form>
          <div className="mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              style={inputStyle}
            />
          </div>
          <div className="mb-3">
            <input
              type="password"
              className="form-control"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={inputStyle}
            />
          </div>
          <button 
            type="button" 
            className="btn btn-primary w-100 mt-3" // Added mt-3 class for margin top
            style={{ borderRadius: '8px',backgroundColor:'green',color:'#fff',position:'absolute' }} // Added inline style for border radius
            onClick={handleLogin}
          >
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

export default LoginPage;
