import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Import Axios for making HTTP requests

const RegisterPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [email, setEmail] = useState('');
  const navigate = useNavigate();

  const handleRegister = async () => {
    try {
      const response = await axios.post('/api/register', { // Change the endpoint to your register endpoint
        username,
        password,
        email
      });

      if (response.status === 201) { // Assuming status 201 indicates successful registration
        alert('User registered successfully.');
        navigate('/login'); // Redirect to login page after successful registration
      } else {
        alert('Registration failed.');
      }
    } catch (error) {
      alert('Registration failed.');
    }
  };

  const backgroundStyle = {
    backgroundImage: `url('https://png.pngtree.com/thumb_back/fh260/background/20220522/pngtree-business-team-banner-task-management-image_1374989.jpg')`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    height: '100vh', 
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  };

  const formContainerStyle = {
    maxWidth: '800px',
    padding: '55px',
    borderRadius: '8px',
    backgroundColor: 'rgba(255, 255, 255, 0.8)', 
    marginRight: '600px', 
    display: 'flex', 
    flexDirection: 'column', 
    alignItems: 'center', 
  };

  const inputStyle = {
    width: '100%', 
    padding: '10px',
    marginBottom: '20px', 
    borderRadius: '5px', 
    border: '1px solid #ccc', 
  };

  return (
    <div style={backgroundStyle}>
      <div className="container" style={formContainerStyle}>
        <h2 className="text-center mb-4">Register</h2>
        <form>
          <div className="mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              style={inputStyle}
            />
          </div>
          <div className="mb-3">
            <input
              type="password"
              className="form-control"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={inputStyle}
            />
          </div>
          <div className="mb-3">
            <input
              type="email"
              className="form-control"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              style={inputStyle}
            />
          </div>
          <button 
            type="button" 
            className="btn btn-primary w-100 mt-3" 
            style={{ borderRadius: '8px', backgroundColor: 'green', color: '#fff', position: 'absolute' }} 
            onClick={handleRegister}
          >
            Register
          </button>
        </form>
      </div>
    </div>
  );
};

export default RegisterPage;
