import React,{useState} from 'react';
import './Dashboard.css'
import { Link, useNavigate} from 'react-router-dom';
import UpcomingTasks from './UpcomingTasks';
import { BsPersonFill } from 'react-icons/bs';
import Reminder from './Reminder';
const Dashboard = () => {
  const navigate = useNavigate();

  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [isReminderOpen, setIsReminderOpen] = useState(false);
  const [isProfileOpen, setIsProfileOpen] = useState(false); 

  const toggleDropdown = () => {
      setIsDropdownOpen(!isDropdownOpen);
  };


  const toggleProfile = () => {
    setIsProfileOpen(!isProfileOpen);
  };


  const handleLogout = () => {
    navigate('/');
  };

  const dropdownStyle = {
      display: isDropdownOpen ? 'block' : 'none',
      position: 'absolute',
      backgroundColor: '#f9f9f9',
      minWidth: '160px',
      boxShadow: '0px 8px 16px 0px rgba(0,0,0,0.2)',
      zIndex: '1',
  };
 

      const headingStyle = {
        position: 'absolute',
        top: '0', 
        left: '0',
        width: '100%',
        textAlign: 'center',
        fontSize: '36px',
        color:'#fff',
        backgroundColor: 'rgb(158, 141, 225 ,0.5)',
        padding: '10px 0', 
        margin: '0', 
        zIndex: '1', 
      };

      const profileStyle = {
        position: 'absolute',
        top: '65px', // Adjust the top position to position below the heading
        right: '0',
        backgroundColor: 'rgb(158, 141, 225,0.9)',
        padding: '20px',
        borderRadius: '10px',
        display: isProfileOpen ? 'block' : 'none',
        zIndex: '1',
        color:'#fff'
        
      };

      const iconContainerStyle = {
        position: 'absolute',
        top: '0',
        right: '0',
        width: '140px',
        height: '65px',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      };

      const iconContainerStyle2 = {
        position: 'absolute',
        top: '0',
        left: '50px',
        width: '240px', 
        height: '65px',
        display: 'flex',
      };

  
      const userDetails = {
        name: 'yamini',
        email: 'yaminid636@gmail.com',
        role: 'User',
      };
      const handleReminderToggle = () => {
        setIsReminderOpen(!isReminderOpen);
      };

  return (
    <div className="dashboard-container">
      <div className="sidenav">
      <h2 style={headingStyle}>
        <div style={iconContainerStyle2}>
          <img
            src="https://www.sweetprocess.com/wp-content/uploads/2023/09/task-management-software-34-1024x511.png"
            alt="Logo"
          />
         </div>
          DashBoard
          <div style={iconContainerStyle} onClick={toggleProfile}>
            <BsPersonFill />
          </div>
        </h2>
        
        <Link to="/add-task">Add Task</Link> 
        <Link to="/all-tasks">All Tasks</Link>
        <a href="#" onClick={handleReminderToggle}>Reminder</a>
        <div className="dropdown">
        <Link to="#" className="dropbtn" onClick={toggleDropdown}>Settings</Link>
                    <div className="dropdown-content" style={dropdownStyle}>
                        <Link to="#">Notifications</Link>
                        <Link to="#">Theme</Link>
                        <Link to="#">Sharing Enable</Link>
                    </div>
                </div>
        <a href="#" onClick={handleLogout}>Logout</a>
      </div>
      <div className="main-content">
      <UpcomingTasks />
      {isReminderOpen && <Reminder />}

      <div style={profileStyle}>
        <h3>User Details</h3>
        <p>Name: {userDetails.name}</p>
        <p>Email: {userDetails.email}</p>
        <p>Role: {userDetails.role}</p>
       <button><Link to="/delete-account">Delete Account</Link></button> 
      </div>
         
      </div>
    </div>
  );
};

export default Dashboard;
