import React from 'react';
import './Dashboard.css'
import { Link, Routes, Route} from 'react-router-dom';
import AllTasks from './AllTasks';
const Dashboard = () => {

  const [showDropdown, setShowDropdown] = useState(false);

  const toggleDropdown = () => {
      setShowDropdown(!showDropdown);
  };

  const dropdownStyle = {
      display: showDropdown ? 'block' : 'none',
      position: 'absolute',
      top: '50px', // Adjust as needed
      left: '0',
      backgroundColor: '#f9f9f9',
      minWidth: '160px',
      boxShadow: '0px 8px 16px 0px rgba(0,0,0,0.2)',
      zIndex: '1',
  };

  const dropdownContentStyle = {
      padding: '12px 16px',
      textDecoration: 'none',
      display: 'block',
      color: 'black',
  };

    
    const headingStyle = {
        position: 'absolute',
        top: '-20px', 
        left: '50%',
        transform: 'translateX(-50%)',
        fontSize: '36px',
        color:'#fff'
      };

      const headingStyle1 = {
        position: 'absolute',
        top: '-10px', 
        left: '16%',
        transform: 'translateX(-150%)',
        fontSize: '30px',
        color:'white' 
      };

  return (
    <div className="dashboard-container">
   
      <div className="sidenav">
        <h2 style={headingStyle1}>DashBoard</h2>
        <Link to="/add-task">Add Task</Link> 
        <Link to="/all-tasks">All Tasks</Link>
        <div style={{ position: 'relative' }}>
                    <Link to="#" onClick={toggleDropdown}>Settings</Link>
                    <div style={dropdownStyle}>
                        <Link to="#" style={dropdownContentStyle}>Notifications</Link>
                        <Link to="#" style={dropdownContentStyle}>Theme</Link>
                        <Link to="#" style={dropdownContentStyle}>Sharing Enable</Link>
                    </div>
                </div>
        <a href="#">Reminder</a>
        <a href="#">Settings</a>
        <Link to="/welcome">Logout</Link> 
      </div>
      <div className="main-content">
        <h1 style={headingStyle}>Welcome to Task Management</h1>
        <Routes>
          <Route path="/all-tasks" element={<AllTasks />} /> {/* Define route for AllTasks component */}
        </Routes>
      </div>
    </div>
  );
};

export default Dashboard;
