import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Import Axios for making HTTP requests

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async () => {
    const credentials = `${username}:${password}`;
    const encodedCredentials = btoa(credentials);

    try {
      const response = await axios.post('http://localhost:8080/api/login', null, {
        headers: {
          'Authorization': `Basic ${encodedCredentials}`,
          'Content-Type': 'application/json'
        },
      });

      if (response.status === 200) {
        // Handle successful login
        navigate('/dashboard');
      } else {
        alert('Invalid username or password.');
      }
    } catch (error) {
      // Handle login failure
      alert('Invalid username or password.');
    }
  };

  const backgroundStyle = {
    backgroundImage: `url('https://png.pngtree.com/thumb_back/fh260/background/20220427/pngtree-financial-analysis-banner-income-statement-image_1092143.jpg')`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    height: '100vh', 
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  };

  const formContainerStyle = {
    maxWidth: '800px',
    padding: '55px',
    borderRadius: '8px',
    backgroundColor: 'rgba(255, 255, 255, 0.8)', 
    marginRight: '600px', 
    display: 'flex', 
    flexDirection: 'column', 
    alignItems: 'center', 
  };

  const inputStyle = {
    width: '100%',    
    padding: '10px', 
    marginBottom: '20px', 
    borderRadius: '5px', 
    border: '1px solid #ccc', 
    marginBottom:'50px',
    marginRight:'30px',
  };

  return (
    <div style={backgroundStyle}>
      <div className="container" style={formContainerStyle}>
        <h2 className="text-center mb-4">Login</h2>
        <form>
          <div className="mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              style={inputStyle}
            />
          </div>
          <div className="mb-3">
            <input
              type="password"
              className="form-control"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={inputStyle}
            />
          </div>
          <button 
            type="button" 
            className="btn btn-primary w-100 mt-3" 
            style={{ borderRadius: '8px',backgroundColor:'green',color:'#fff' }} 
            onClick={handleLogin}
          >
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

export default LoginPage;
