import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import UserService from '../services/UserService';

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      await UserService.login({ username, password });
      // If login is successful, redirect to a dashboard or home page
      navigate('/dashboard');
    } catch (error) {
      alert('Invalid username or password.');
    }
  };

  const backgroundStyle = {
    backgroundImage: `url('https://png.pngtree.com/thumb_back/fh260/background/20220427/pngtree-financial-analysis-banner-income-statement-image_1092143.jpg')`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    height: '100vh',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    color: '#fff', // Set text color to white for contrast
    textAlign: 'center'
  };

  const formStyle = {
    backgroundColor: 'rgba(0, 0, 0, 0.5)', // Semi-transparent black background
    padding: '20px',
    borderRadius: '8px'
  };

  const inputStyle = {
    width: '100%',
    marginBottom: '10px',
    padding: '8px',
    borderRadius: '4px',
    border: 'none',
    backgroundColor: 'rgba(255, 255, 255, 0.1)', // Semi-transparent white background
    color: '#fff'
  };

  const buttonStyle = {
    width: '100%',
    padding: '10px',
    borderRadius: '4px',
    border: 'none',
    backgroundColor: '#007bff', // Bootstrap primary color
    color: '#fff',
    cursor: 'pointer'
  };

  return (
    <div style={backgroundStyle}>
      <div style={formStyle}>
        <h2>Login</h2>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={inputStyle}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={inputStyle}
        />
        <button onClick={handleLogin} style={buttonStyle}>Login</button>
      </div>
    </div>
  );
};

export default LoginPage;
