import React,{useState} from 'react';
import './Dashboard.css'
import { Link, Routes, Route,useNavigate} from 'react-router-dom';
import AllTasks from './AllTasks';
const Dashboard = () => {
  const navigate = useNavigate();

  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const toggleDropdown = () => {
      setIsDropdownOpen(!isDropdownOpen);
  };

  const handleLogout = () => {
   
    navigate('/');
  };

  const dropdownStyle = {
      display: isDropdownOpen ? 'block' : 'none',
      position: 'absolute',
      backgroundColor: '#f9f9f9',
      minWidth: '160px',
      boxShadow: '0px 8px 16px 0px rgba(0,0,0,0.2)',
      zIndex: '1',
  };
  const headingStyle = {
    position: 'absolute',
    top: '-25px', 
    left: '0',
    width: '100%', // Set the width to cover the entire container
    textAlign: 'center', // Align text to the center
    fontSize: '36px',
    color:'#fff',
    backgroundColor: 'rgb(158, 141, 225)',
    padding: '10px 0', 
  Align: 'center',
      };

      const headingStyle1 = {
        position: 'absolute',
        top: '0', 
        left: '0',
        width: '100%',
        textAlign: 'center',
        fontSize: '36px',
        color:'#fff',
        backgroundColor: 'rgb(158, 141, 225)',
        padding: '10px 0', 
        margin: '0', // Remove default margin
        zIndex: '1', // Ensure the heading is above other elements
      };
    

  return (
    <div className="dashboard-container">
   
      <div className="sidenav">
        <h2 style={headingStyle1}>DashBoard</h2>
        <Link to="/add-task">Add Task</Link> 
        <Link to="/all-tasks">All Tasks</Link>
        <a href="#">Reminder</a>
        <div className="dropdown">
        <Link to="#" className="dropbtn" onClick={toggleDropdown}>Settings</Link>
                    <div className="dropdown-content" style={dropdownStyle}>
                        <Link to="#">Notifications</Link>
                        <Link to="#">Theme</Link>
                        <Link to="#">Sharing Enable</Link>
                    </div>
                </div>
        
        
        <a href="#" onClick={handleLogout}>Logout</a>
      </div>
      <div className="main-content">
        <h1 style={headingStyle}>Welcome to Task Management</h1>
         <Routes>
          <Route path="/all-tasks" element={<AllTasks />} /> {/* Define route for AllTasks component */}
        </Routes>
      </div>
    </div>
  );
};

export default Dashboard;
