import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import UserService from '../services/UserService';

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      await UserService.login({ username, password });
      // If login is successful, redirect to a dashboard or home page
      navigate('/dashboard');
    } catch (error) {
      alert('Invalid username or password.');
    }
  };

  const backgroundStyle = {
    backgroundImage: `url('https://png.pngtree.com/thumb_back/fh260/background/20220427/pngtree-financial-analysis-banner-income-statement-image_1092143.jpg')`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    height: '100vh', // Set the height of the background to fill the viewport
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  };

  const formContainerStyle = {
    maxWidth: '400px',
    padding: '40px',
    borderRadius: '8px',
    backgroundColor: 'rgba(255, 255, 255, 0.8)', // Semi-transparent white background
    marginRight: '500px', // Adjust the left margin as needed
  };

  const inputStyle = {
    width: '100%', // Make input boxes take up full width
    padding: '10px', // Add padding for better appearance
    marginBottom: '20px', // Add some space between input boxes
    borderRadius: '5px', // Add rounded corners
    border: '1px solid #ccc', // Add border for better visibility
  };

  return (
    <div style={backgroundStyle}>
      <div className="container" style={formContainerStyle}>
        <h2 className="text-center mb-4">Login</h2>
        <form>
          <div className="mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              style={inputStyle}
            />
          </div>
          <div className="mb-3">
            <input
              type="password"
              className="form-control"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={inputStyle}
            />
          </div>
          <button type="button" className="btn btn-primary w-100" onClick={handleLogin}>Login</button>
        </form>
      </div>
    </div>
  );
};

export default LoginPage;
